module.exports = function tiny(string1, string2) {
    
    // --> Verify args
    if(!string1 || !string2) {
        throw new TypeError('Command not entered...');
        return
    }
    if (typeof string1 !== "string" || typeof string2 !== "string") {
        throw new TypeError("Tiny wants a string!");
        return
    }

    // --> Process
    if(string1.toLowerCase() != string2.toLowerCase()) {
        return false
    } else {
        return true
    }

};